ValidationRule.errorConditionFormula:
/* Evaluate in these conditions */ ( (/* Exempt new imported records */ !ISNEW() && !ISBLANK(TEXT (Imported_From__c)) ) ||
/* Or the field value has changed */
ISCHANGED ( Residential_Phone__c )
)
&& /* Core of validation rule */
!ISBLANK ( Residential_Phone__c ) && !ISNUMBER ( Residential_Phone__c ) && !ISPICKVAL ( Residential_Country__c , 'United States of America')
||
AND( !ISBLANK ( Residential_Phone__c ), !REGEX(Residential_Phone__c, "\\D*?(\\d\\D*?){10}"), ISPICKVAL ( Residential_Country__c , 'United States of America'))
